// ðŸ”¹ Manual ASCII-based approach
var toLowerCase = function (s) {
  let str = "";

  for (let i = 0; i < s.length; i++) {
    let charCode = s[i].charCodeAt(0);
    if (charCode >= 65 && charCode <= 90) {
      str += String.fromCharCode(charCode + 32);
    } else {
      str += s[i];
    }
  }

  return str;
};

