var removeDuplicates = function (nums) {
  let first = 0;
  let sec = 1;

  while (sec < nums.length) {
    if (nums[first] === nums[sec]) {
      sec++;
    } else {
      first++;
      [nums[first], nums[sec]] = [nums[sec], nums[first]];
      sec++;
    }
  }

  console.log(nums.slice(0, first + 1)); // Shows the unique values
  return first + 1;
};

